  function handler(event)
  {

console.log(event.target.value)
 setData(event.target.value);
return(<div>

<p>you have entered {data}</p>
  
</div>)
  }




return(<div  className="Box3"  >
   <Nvabar1></Nvabar1>
<center>
  <fieldset > 
<h1> Payment</h1>
<br></br>
